targets.expr:
sum(
    node_namespace_pod_container:container_cpu_usage_seconds_total:sum_irate{namespace=~"gloo-mesh-addons|istio-gateways"}
  * on(namespace,pod)
    group_left(workload, workload_type) namespace_workload_pod:kube_pod_owner:relabel{namespace=~"gloo-mesh-addons|istio-gateways", workload_type="deployment"}
) by (pod)

sum(
    container_memory_working_set_bytes{namespace=~"gloo-mesh-addons|istio-gateways", container!="", image!=""}
  * on(namespace,pod)
    group_left(workload, workload_type) namespace_workload_pod:kube_pod_owner:relabel{namespace=~"gloo-mesh-addons|istio-gateways", workload_type="deployment"}
) by (pod)
